mapper.insert:
INSERT INTO disney_contents(
    imdb_id
    , title
    , plot
    , type
    , rated
    , year
    , released_at
    , runtime
    , director
    , writer
    , actor
    , subtitle
    , award_win
    , award_nominate
    , award_major
    , award_major_type
    , imdb_rating
    , imdb_votes
    , rating
    , review_cnt
    , likes_cnt
) VALUES (
    #{contentId}
    , #{title}
    , #{plot}
    , #{type}
    , #{rated}
    , #{year}
    , #{releasedAt}
    , #{runtime}
    , #{director}
    , #{writer}
    , #{actor}
    , #{subtitle}
    , #{awardWin}
    , #{awardNominate}
    , #{awardMajor}
    , #{awardMajorType}
    , #{imdbRating}
    , #{imdbVotes}
    , #{rating}
    , #{reviewCnt}
    , #{likesCnt}
)

mapper.update:
UPDATE disney_contents
SET title = #{title}
WHERE imdb_id = #{contentId}

UPDATE disney_contents
SET review_cnt = review_cnt + #{amount}
WHERE imdb_id = #{contentId};

UPDATE disney_contents
SET likes_cnt = likes_cnt + #{amount}
WHERE imdb_id = #{contentId};

mapper.select:

SELECT genre
FROM disney_genre
WHERE imdb_id = #{contentId}
ORDER BY genre ASC;
