mapper.insert:
INSERT INTO disney_review (
    content_id
    , review_text
    , review_writer
    , rating
) VALUES (
    #{contentId}
    , #{reviewText}
    , #{reviewWriter}
    , #{reviewRating}
)

mapper.update:
UPDATE disney_review
SET
    review_text = #{reviewText}
    , update_date = NOW()
    , rating = #{reviewRating}
WHERE review_no = #{reviewNo}

mapper.select:
SELECT content_id
FROM disney_review
WHERE review_no = #{reviewNo}

SELECT COUNT(content_id)
FROM disney_review
WHERE content_id = #{contentId}
